"""
this piece of code is for backward compatibility support
first the installation of respective tool utility shall be done here
if the tool utility library is not installed then it will be detected
and installation of tool utility library shall be done
"""
import os
# un-used import flake issue ignored as this file is for installing latest available library
# flake8: noqa: F401
try:
    from ptf.tools_utils.common import log_backward_compatibility_prints

    log_backward_compatibility_prints(lib_name=os.path.dirname(__file__).split(os.sep)[-1])
    import contest_doip
except ImportError:
    from uim.uim import install_contest_pkg_for_backward_compatibility_support
    from global_vars import __TOOL_STARTED

    # only install the library if contest_tool started
    # this is to avoid PyCharm to install library automatically while indexing or updating skeleton background tasks
    if __TOOL_STARTED:
        install_contest_pkg_for_backward_compatibility_support("contest_doip")
