"""
Code for reading the version of currently available contest package & informing user with an initial print statement.
"""

from pathlib import Path

try:
    from importlib.metadata import PackageNotFoundError, version
except ImportError:  # for Python<3.8
    from importlib_metadata import PackageNotFoundError, version

__package_name = Path(__file__).parent.name
__PACKAGE_VERSION = None
try:
    __PACKAGE_VERSION = version(__package_name)
except PackageNotFoundError:
    print(f"Package '{__package_name}' not found")
